import React, { Component } from 'react';
import Home from './Home.js';


class MainInfo extends Component{
  render() {
    // const d = this.props.details
    const actIn = this.props.actIn

    // const dn = [Home, About, Projects]
    //
    // console.log(d)
    // for (var i = 0; i<3; i++){
    //   console.log(dn[i])
    //   Object.assign({key3: "value3"})
    //   dn[i].div = dn[i]
    //   console.log(d)
    // }
    // console.log(d)
    //
    // const DivName = Home

    const d = [
      {id:'home', label:'Home', divName: Home},
      {id:'about', label:'About', divName: Projects},
      {id:'projects', label:'Projects', divName: Home},
    ]

    return(
      <div>
        { d.map((details, i) => {
          return(
            <BoxInfo
              index={i}
              key={details.id}
              id={details.id + '-content'}
              actIn={actIn}
              title={details.label}
              divName={details.divName}
            />
          )
        }) }



      </div>
    )

  }
}

class BoxInfo extends Component{


  render() {

    return(
    <div id={this.props.id}
    className={(this.props.actIn === this.props.index ? 'visible' : 'invisible')}>

      {this.props.title}
      { <this.props.divName /> }
    </div>
    )
  }
}


class About extends Component {
  render() {

    return(
    <div className="animated fadeInLeft">
      project links
    </div>
    )
  }
}
class Projects extends Component {
  render() {

    return(
    <div className="animated fadeInLeft">
      all the stuff for projects
    </div>
    )
  }
}

export default MainInfo;
