import React, { Component } from 'react';
import './App.css';

class App extends Component {
  render() {
    return (
      <div className="App">
        <Main />
      </div>
    );
  }
}

class Main extends Component{
  render() {
    return(
      <div className='main'>
        <Buttons />
        <MainInfo />
      </div>
    )
  }
}



class MainInfo extends Component{
  render() {
    return(
      <article className='about-div'>
        <h1>Name Lastname</h1>
        About
      </article>
    )
  }
}

class Buttons extends Component{
  constructor(props) {
    super(props);

    this.state = {
      details: [
        {id:'home', label:'Home'},
        {id:'about', label:'About'},
        {id:'projects', label:'Projects'},
      ],
    };

  }

  render() {
    const btnsArr = this.state.details.map((details) => {
      return(
        <MainButton
          key={details.id}
          id={details.id}
          label={details.label}
        />
      )
    })

    return(
      <div>
        {btnsArr}
      </div>
    )
  }
}

class MainButton extends Component{
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    var select = document.getElementById(this.props.id)
    var el = document.getElementsByClassName('buttons')
    for (var i = 0; i < el.length; i++ ) {
      el[i].classList.remove('visible')
      el[i].classList.add('invisible')
    }
    select.classList.remove('invisible')
    select.classList.add('visible')
  }

  render() {
    return(
      <button className='btn buttons' id={this.props.id} onClick={this.handleClick}>
         {this.props.label}
      </button>
    )
  }
}


export default App;
