import React, { Component } from 'react';
import './App.css';

class App2 extends Component {
  constructor(props) {
    super(props)
  }

  render() {
    return (
      <div className="App">
        <Main />
      </div>
    );
  }
}

class Main extends Component{
  render() {
    return(
      <div className='main'>
        <Buttons />
      </div>
    )
  }
}

class Buttons extends Component{
  constructor(props) {
    super(props);
    this.state = {
      details: [
        {id:1, label:'Home'},
        {id:2, label:'About'},
        {id:3, label:'Projects'},
      ]
    }
  }

  render() {
    const btnsArr = this.state.names.map((details) => {
      return(
        <MainButton
          id={details.id}
          label={details.label}
        />
      )
    })

    return(
      <div>
        {btnsArr}
      </div>
    )
  }
}

class MainButton extends Component{
  render() {
    return(
      <a>
        <button className='btn buttons'>
          {this.props.id} {this.props.label}
        </button>
      </a>
    )
  }
}

